import * as React from "react";
import pageData from "./DayView";

export default function Home() {
  const [count, setCount] = React.useState("test");
  let increment = () => {
    setCount(count + 1);
  };
  let decrement = () => {
    setCount(count + 1);
  };
  return (
    <div>
      <main>
        <Table />
      </main>
    </div>
  );
}

type Pages = [pageData];
class Table extends React.Component {
  state: { pages: Pages };
  constructor(props: any) {
    super(props);
    // let p = [] as never;
    let p = [{ goals: "test", table: [{}], Date: new Date() }] as never;
    this.state = { pages: p };

    fetch("/getall").then((data) => {
      data.json().then((data) => {
        this.setState({ pages: data.data });
      });
    });
  }

  render(): React.ReactNode {
    return (
      <table>
        <thead><td>Total HRS</td><td>Summary</td><td>row ID</td></thead>
        <tbody><div>{this.state.pages.map((day, ID, a) => {
          let tally = 0
          if (day.table != undefined){
          for(let hrs of day.table){
            tally += hrs.hrs
          }
        }
          return(<tr id={ID.toString()}><td>{day.goals}</td><td>{ID}</td>tally</tr>)
        })}</div></tbody>
      </table>
    );
  }
}
