import * as React from "react";
import pageData from "./DayView";

export default function Home() {
  const [count, setCount] = React.useState("test");
  let increment = () => {
    setCount(count + 1);
  };
  let decrement = () => {
    setCount(count + 1);
  };
  return (
    <div>
      <main>
        <Table />
      </main>
    </div>
  );
}

type Pages = [pageData];
class Table extends React.Component {
  state: { pages: Pages };
  constructor(props: any) {
    super(props);
    // let p = [] as never;
    let p = [{ goals: "test", table: [{}], Date: new Date() }] as never;
    this.state = { pages: p };

    fetch("/getall").then((data) => {
      data.json().then((data) => {
        this.setState({ pages: data.data });
      });
    });
  }

  render(): React.ReactNode {
    return (
      <table>
        <thead>
          <td>Summary</td>
          <td>Total HRS</td>
          <td>row ID</td>
        </thead>
        <tbody>
          {this.state.pages.map((page, ID, a) => {
            let tally = 0;
            console.log(page.table)
            if (page.table != undefined){
              for (let row of page.table) {
                // console.log(row.hrs)
                if (row.hrs != NaN){
                tally += row.hrs;
                }
              }
            }
            return (
              <tr id={ID.toString()}>
                <td>{page.goals}</td>
                <td>{tally}</td>
                <td>{ID}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  }
}
