import * as React from "react";
import { Link } from "react-router-dom";
import "./App.css";
type props = { formID: string };

export class DayView extends React.Component {
  constructor(props: any, state: any) {
    super(props, state);
  }
  render(): React.ReactNode {
    return (
      <>
        <main>
          <h2>July 1st</h2>
          <ActivitiesTable />
          <Form formID="Goals" />
          <Form formID="Summary" />
        </main>
        <nav>
          <Link to="/">Home</Link>
        </nav>
      </>
    );
  }
}

class ActivitiesTable extends React.Component {
    state: {table: [{hrs: number, activity: string}]}

    constructor(state: any, props: null){
        super(state)
        this.state = {table: [{hrs: 2,activity:'test'}]}
    }
  render(): React.ReactNode {
    return (
      <div>
        <table className="activitiesTable">
          <thead>
            <td>HRS</td>
            <td>description</td>
            <td></td>
          </thead>
          <tbody>
            <tr id="row">
              <td>3</td>
              <td>{this.state.table[0].activity}</td>
              <td>
                {" "}
                <button>X</button>
              </td>
            </tr>
          </tbody>
          <tfoot>
            <tr>
              <td>
                <form>
                  <input type="text" value="4"></input>
                </form>
              </td>
              <td>
                <input type="text" value="brainstorming"></input>
              </td>
              <td></td>
            </tr>
            <tr>
              <td>Total HRS: 7</td>
              <td> </td>
              <td>
                <button>+</button>
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
    );
  }
}

class Form extends React.Component {
  props: props;
  state: { value: string };
  formID: string;
  constructor(props: props) {
    super(props);
    this.props = props;
    this.formID = props.formID;
    this.state = { value: "" };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event: any) {
    this.setState({ value: event.target.value });
    console.log(this.state.value);
  }

  handleSubmit(event: any) {
    console.log(this.formID + " " + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>{this.formID}</label>
        <br />
        <textarea value={this.state.value} onChange={this.handleChange} />
        <br />
        <input type="submit" value="Submit" />
      </form>
    );
  }
}
